module.exports = {
    hooks: {
        'init': function() {
            global._hooks = [];
            global._hooks.push('init');
        },
        'finish': function() {
            global._hooks.push('finish');
        },
        'finish:before': function() {
            global._hooks.push('finish:before');
        },
        page: function(page) {
            page.content = 'Hello ' + page.content;
            return page;
        }
    }
};
